import org.apache.spark.streaming.receiver.Receiver
import org.apache.spark.internal.Logging
import org.apache.spark.storage.StorageLevel
import scala.util.Random

class FlightReplyData(timeBreakSec : Double) extends org.apache.spark.streaming.receiver.Receiver[String](org.apache.spark.storage.StorageLevel.MEMORY_AND_DISK_2) { 

  def onStart() {
    // Start the thread that receives data over a connection
    new Thread("Socket Receiver Flight") {
      override def run() { receive() }
    }.start()
  }

  def onStop() {
   // There is nothing much to do as the thread calling receive()
   // is designed to stop by itself isStopped() returns false
  }

  /** Create a socket connection and receive data until receiver is stopped */
  private def receive() {
   var lastTime = ""
   var resultArray = List[String]()
   while(!isStopped()) {     
        //Lets create an array to return as stream per minute (as our set is ordered by minute (event time)
        flightArray.foreach(r => {
            resultArray =  (r(0) + "," + r(1) + "," + r(2) + "," + r(3) ).toString :: resultArray
            
            if (r(1).toString != lastTime) {
                lastTime = r(1).toString
                store(resultArray.reverse.iterator) //reverse to start with oldest time
                resultArray = List[String]()
                Thread.sleep((timeBreakSec * 1000).toLong) //break to give stream some air
            }
            
        }) 
       
    }
  }
}

