    //}).addTo(map);
    L.tileLayer('http://{s}.google.com/vt/lyrs=p&x={x}&y={y}&z={z}',{
        maxZoom: 20,
        subdomains:['mt0','mt1','mt2','mt3']
    }).addTo(map);


    //subdomains:['mt0','mt1','mt2','mt3']            
    var geoMarkers = L.layerGroup().addTo(map); //Ship icons to be stored in layer, so we can clear all of them each update

    var el = angular.element($('#mapt3').parent('.ng-scope'));
    angular.element(el).ready(function() {
        window.locationWatcher = el.scope().compiledScope.$watch('planelocations', function(newValue, oldValue) {
            geoMarkers.clearLayers(); //Remove the ship icons at their current location.

            var isTimeSet = false

            angular.forEach(newValue, function(event) {
                if (event) {
                     if (isTimeSet == false) {
                        //lets add time/minute in middle of map
                            var divIcon = L.divIcon({ 
                            html: '<span style="color:white;font-size: 15pt" class="my-div-span">' + event.values[3] + '</span>'
                        });
                        L.marker(new L.LatLng(28.45900, -45.7509), {icon: divIcon }).addTo(geoMarkers);
                        isTimeSet = true;
