import org.apache.spark.streaming.{Milliseconds,Seconds, StreamingContext}
import org.apache.spark.SparkContext._
import org.apache.spark.rdd.RDD
import org.apache.spark.sql.expressions.Window

z.angularUnbind("planelocations") //Our geo map in the previous section will be updating  once we update this Zeppelin context variable

StreamingContext.getActive.foreach { _.stop(stopSparkContext = false) }
val ssc = new StreamingContext(sc, Milliseconds(500)) //Our stream will update each second 
val flightDStream = ssc.receiverStream(new FlightReplyData(0.4)) 

//val windowFlightDStream = flightDStream

// Use a Window Dstream with location of couple of runs . Required if you want to aggregate over a longer historical period or prevent duplicates/or dissapearing planes. 
val windowFlightDStream = flightDStream.window(Milliseconds(1000),Milliseconds(500)) 

//Get the latest location of each plane (otherwise we would see double, or missings planes on the map). 
//and return it to our Leaflet using a Zepplin Variable named locations (Expecting Array of arrays Name,lat,lon)                                                        
windowFlightDStream.foreachRDD { rdd =>  

                        val inputDf = rdd.map (s => s.split(","))
                                    .map(arr => (arr(0),arr(1),arr(2),arr(3)) )
                                    .toDF("id","timest","lat","lon").na.drop
                        //option 1 just have a quick but flickering, missing animation
                        //z.angularBind("planelocations", inputDf.select($"id",$"lat",$"lon",$"timest").collect)

                        //option 2 group by plane and get the latest location
                        //val maxTimeStampDf = inputDf.groupBy($"id").agg(max($"timest") as "maxtimestamp").withColumnRenamed("id", "id2")
                        //val latestLocationDf = inputDf.join(maxTimeStampDf,inputDf("id") === maxTimeStampDf("id2")).filter($"timest" === $"maxtimestamp")  //.orderBy($"id".asc,$"timestamp".asc).cache
                        //z.angularBind("planelocations", latestLocationDf.select($"id",$"lat",$"lon",$"timest").collect)

                        //Option 3 get a minute field from 1 row and filter all rows with it (and make sure the data is in a bit quicker then the stream interval)
                        val window = Window.partitionBy($"id").orderBy($"timest".asc)
                        z.angularBind("planelocations", inputDf.withColumn("rank", row_number().over(window)).where($"rank" === 1).select($"id",$"lat",$"lon",$"timest").collect  ) //.where($"rank" === 1)


}                       

ssc.start()

// This is to ensure that we wait for some time before the background streaming job starts. 
ssc.awaitTerminationOrTimeout(2000)

